mapper.update:
UPDATE TRAVEL
SET
PARENTBOARD=#{parentBoard}
WHERE BOARDNUM=#{boardNum}

UPDATE TRAVEL
SET
CHILDBOARD=#{childBoard}
WHERE BOARDNUM=#{parentBoard}

mapper.select:
SELECT BOARDNUM, PARENTBOARD, CHILDBOARD, ROOTBOARD
FROM TRAVEL
WHERE BOARDNUM=#{boardNum}

mapper.update:
UPDATE TRAVEL
SET
ROOTBOARD=#{rootBoard}
WHERE BOARDNUM=#{boardNum}
